#include<bits/stdc++.h>
using namespace std;
int n,t,ans[5000001];//用数组模拟路灯，由于题中没有具体数值，所以开大一点
double a;
int main(){
  cin>>n;//输入
  for(int i=1;i<=n;i++){
     cin>>a>>t;
    for(int j=1;j<=t;j++){

      int num=j*a;//将乘积换成整形（是乘积！！）
      if(ans[num]==0)ans[num]=1;//0代表关，1代表开
      else ans[num]=0;//转换（1换0,0换1）
    }
  }
