for(int i=1;i<=n;i++)if(f[i][n]==0)bfs(i,n);//第n列
for(int i=1;i<=n;i++)if(f[1][i]==0)bfs(1,i);//第一行
for(int i=1;i<=n;i++)if(f[n][i]==0)bfs(n,i);//第n行
