```cpp
#include<bits/stdc++.h>//万能头，懒人专用 
using namespace std;
int f[32][32];
int dx[4]= {0,1,0,-1},dy[4]= {1,0,-1,0};//存储方向
int n;
queue<int>q;//使用stl队列
void bfs(int x,int y) {//广搜
    int x1,x2,y1,y2;//这里别学我开y1变量，有时候会被判断为一个函数y1，然后gg 
    q.push(x);//存储x(下同) 
    q.push(y);//存储y(下同) 
    f[x][y]=2;//染色(下同) 
    while(!q.empty()) {//队列不为空 
        x1=q.front();
        q.pop();
        y1=q.front();
        q.pop();//获得队头数据 
        for(int i=0; i<4; i++) {//向四个方向搜索
            x2=x1+dx[i];
            y2=y1+dy[i];//用变量，下文判断更方便 
            if(x2>=0&&x2<=n+1&&y2>=0&&y2<=n+1&&f[x2][y2]==0) { 
                q.push(x2);
                q.push(y2);
                f[x2][y2]=2;
            }//满足条件，入队
        }
    }
    return;
}
int main() {
    cin>>n;
    for(int i=1; i<=n; i++)
        for(int j=1; j<=n; j++) {
            cin>>f[i][j];
        }
    bfs(0,0);//从(0,0)开始，一定能搜到外圈省去多余枚举外圈代码
    for(int i=1; i<=n; i++) {
        for(int j=1; j<=n; j++)
            cout<<(f[i][j]==0?2:(f[i][j]==1?1:0))<<' ';//装B专用写法，意为如果为0输出2，否则如果为1输出1，否则输出0
        cout<<endl;//别忘记输出回车 
    }
    return 0;//好习惯 
}
