struct node {int x,y;};
void BFS(int x,int y)//常规操作 
{  queue<node>q;
   node t={x,y};
   q.push(t); 
   vst[x][y]=1;
   while(!q.empty())
   {  int nx=q.front().x,ny=q.front().y;
      for(int i=0;i<4;i++)
      {  int tx=nx+dx[i],ty=ny+dy[i];
         if(tx<1||tx>n||ty<1||ty>n){bj=1;continue;}//如果是边界，标记 
         if(!vst[tx][ty])
