 # 于是就有了正解
我们将vst数组增加一个用途，记录为第几次搜到的点（这不好影响搜索本身），用一个sign去更新它。
```cpp
#include<iostream>
#include<iomanip>
#include<cstring>
#include<cstdio>
#include<cmath>
#include<algorithm>
#include<queue>
using namespace std;
int n,a[1005][1005],vst[1005][1005],sign=1;//因为原图上为1就标记1，所以第一次搜索时标记为2 
int dx[4]={1,0,-1,0},dy[4]={0,1,0,-1};
struct node {int x,y;};
int BFS(int x,int y)//还是常规操作 
{  queue<node>q;
   node t={x,y};
   q.push(t); 
   sign++;
   vst[x][y]=sign;
   int bj=1;//初始为1 
   while(!q.empty())
   {  int nx=q.front().x,ny=q.front().y;
      for(int i=0;i<4;i++)
      {  int tx=nx+dx[i],ty=ny+dy[i];
         if(tx<1||tx>n||ty<1||ty>n){bj=0;continue;}//如果搜到边界，bj为假。 
         if(!vst[tx][ty])
         {  node t={tx,ty};
            q.push(t);
            vst[tx][ty]=sign; //标记第几次搜 
      }

   }q.pop();
}
   return bj;
}
int main()
{  cin>>n;
   for(int i=1;i<=n;i++)
     for(int j=1;j<=n;j++)
     {  scanf("%d",&a[i][j]);
        if(a[i][j])vst[i][j]=1;//原图上为1就标记1 
     }

   for(int i=2;i<=n;i++)
     for(int j=2;j<=n;j++)//每一个点都搜索，因为在边缘的点必定搜到标记，所以从2开始。 
       if(!vst[i][j])//如果这个点不是1. 
         if(BFS(i,j))//返回真表示搜到染色块，sign会保存的是染色连通块的标记。 
         {  for(int i=1;i<=n;i++)
            {  for(int j=1;j<=n;j++)
                 if(vst[i][j]==sign)printf("2 ");//如果点的标记等于sign，染色。 
                 else printf("%d ",a[i][j]);
                printf("\n");
            }
         }
   return 0;
 } 
