#include<bits/stdc++.h>
using namespace std;
int a[35][35],n,b[35][35],f;
int fx[4]={-1,0,0,1},fy[4]={0,-1,1,0};
int TP(int x)
{
	if(x<0||x==n) return 1;
	return 0;
}
inline void dfs1(int x,int y)
{
	if(b[x][y]) return;
	if(TP(x)||TP(y)) 
	{
		f=1;
		return;
	}
	b[x][y]=2;
	for(int i=0;i<4;i++)
		dfs1(x+fx[i],y+fy[i]);
}
inline void dfs2(int x,int y)
{
	if(a[x][y]) return;
	a[x][y]=2;	
	for(int i=0;i<4;i++)
		dfs2(x+fx[i],y+fy[i]);
} 
int main()
{
	int i,j,x,y,flag;
	cin>>n;
	for(i=0;i<n;i++)
		for(j=0;j<n;j++)
		{
			cin>>a[i][j];
			if(a[i][j]==0)
			{
				x=i;
				y=j;
			}
			b[i][j]=a[i][j];
		}	
	for(;;)
	{
		f=0;
		flag=0;
		dfs1(x,y);
		if(f==0) break;
		for(i=0;i<n;i++)
		{
			for(j=0;j<n;j++)
				if(!b[i][j])
				{
					x=i;
					y=j;
					flag=1;
					break;
				}
			if(flag) break;
		}		
	}	
	dfs2(x,y);
	for(i=0;i<n;i++)
	{
		for(j=0;j<n;j++)
			cout<<a[i][j]<<' ';
		cout<<endl;
	}
} 
