一种另类的解法（至少题解里没有dalao和我一样）

**正面硬杠**，

读入MAP后寻找出第一个在“围墙”里的0，从这个0开始bfs即可！










```cpp
#include<cstdio>
#include<queue>
#include<iostream>
using namespace std;
int n;
int map[32][32];
void bfs(int x,int y);
struct node{
    int x;
    int y;
};
int main()
{
    cin>>n;
    int i,j;
    int flag=0,fllag=0;
    int startx,starty;
    for(i=1;i<=n;i++)
        for(j=1;j<=n;j++)
            cin>>map[i][j];
    for(i=2;i<=n;i++)               //开始寻找第一个围墙里的0
    {    
        for(j=1;j<n;j++)
            if(map[i][j]==1 && map[i][j+1]==0) {flag=j;    
        for(j=flag;j<n;j++) 
            if(map[i][j]==0 && map[i][j+1]==1) fllag=j;
        }
        if(fllag!=0) 
        {startx=i;starty=fllag;break;}
    }
    //查找完毕（startx,starty），开始BFS！
    map[startx][starty]=2;
    bfs(startx,starty);
    for(i=1;i<=n;i++)
    {    for(j=1;j<=n;j++)
            cout<<map[i][j]<<" ";
        cout<<endl;
    }
//    printf("\n%d %d",startx,starty);
    return 0;
}
void bfs(int x,int y)
{
    queue<node>Q;
    node vs;
    vs.x=x;vs.y=y;
    Q.push(vs);
    int nnext[4][2]={{0,1},{1,0},{0,-1},{-1,0}};
    int tx,ty,flag;
    while(!Q.empty())
    {
        for(int k=0;k<4;k++)
        {
            tx=Q.front().x+nnext[k][0];
            ty=Q.front().y+nnext[k][1];
            if(tx<1||ty<1||tx>n||ty>n||map[tx][ty]==1||map[tx][ty]==2) continue;
            vs.x=tx;vs.y=ty;Q.push(vs);
            map[tx][ty]=2;
        }
        Q.pop();
    }
}
```

