int a[50][50],flag;//a是方阵的标记数组，flag是状态标记
int main()
{
    scanf("%d",&n);
for(int i=1;i<=n;++i)
        for(int j=1;j<=n;++j)
            scanf("%d",&c[i][j]);//输入数据
    for(int i=1;i<=n;++i)
    {
        for(int j=1;j<=n;++j)
        {
            if(a[i-2][j]!=2&&c[i-2][j]!=1)//判断上方有没有在闭合圈内
                if(a[i-1][j]==2)
                    a[i-1][j]=0;    //不在就把标记去掉
        }
        if(flag==2)//当上一行无1做结尾时：
        {
            for(int k=n;k>0;--k)//倒着找上一行直到找到一个1，并把标记去掉
            {
                if(c[i-1][k]==1)
                    break;
                if(a[i-1][k]==2)
                    a[i-1][k]=0;    
            }
        }
        flag=0;//每行从头开始时把标记重置成0
        for(int j=1;j<=n;++j)
        {
            if(c[i][j]==1&&flag!=2)
                flag=1;//找到1时把标记记为1
            if(c[i][j]==0&&(flag==1||flag==2))
            {
                a[i][j]=2;//在标记不为0的情况下把扫到的0记上标记
                flag=2;
            }
            if(flag==2&&c[i][j]==1)
                flag=1;
        }
    }
    for(int i=n;i>=1;--i)//倒着搜一遍标记
    {
        for(int j=1;j<=n;++j)
        {
            if(a[i+1][j]!=2&&c[i+1][j]!=1)//判断下方在不在闭合圈内
                if(a[i][j]==2)
                    a[i][j]=0;//不在去掉标记
        }
    }
    if(flag==2)//正着搜索打标记时没有判断最后一行这里在判断下
        {
            for(int k=1;k<=n;++k)
                if(a[n][k]==2)
                    a[n][k]=0;    
        }
    for(int i=1;i<=n;++i)
    {
        for(int j=1;j<=n;++j)//输出
        {
            if(a[i][j]==2)//如果有标记就输出2
                printf("2 ");
            else
                printf("%d ",c[i][j]);//没有标记就输出原数
        }
    printf("\n");//换行
    }
    return 0;
}
//可能标记标记有点乱
