  var
    head,tail,x,y,i,j:longint;
    a:array[0..901,1..2] of longint;
  begin
    a[1,1]:=xx;
    a[1,2]:=yy;
    head:=0;
    tail:=1;
    while head<tail do
    begin
      inc(head);
      for i:=1 to 4 do
