        x:=a[head,1]+dx[i];
        y:=a[head,2]+dy[i];
        if (map[x,y]=2) and (x<=n) and (x>0) and (y<=n) and (y>0) then
        begin
          inc(tail);
          a[tail,1]:=x;
          a[tail,2]:=y;
          map[x,y]:=0;
        end;
      end;
    end;
  end;
begin
  init;
  for j:=1 to n do
  begin
    if map[j,1]=2 then bfs(j,1);
    if map[j,n]=2 then bfs(j,n);
    if map[1,j]=2 then bfs(1,j);
    if map[n,j]=2 then bfs(n,j);
  end;
  for i:=1 to n do
  begin
    for j:=1 to n do
      write(map[i,j],' ');
