            if (temp.x<=0||temp.x>n||temp.y<=0||temp.y>n) continue;//边界
            map[temp.x][temp.y]=2;
            if (map[temp.x][temp.y-1]==0) {l++;list[l].x=temp.x;list[l].y=temp.y-1;}
