```cpp
#include <iostream>
#include <cstdio>
#include <cstdlib>
#include <cstring>
#include <algorithm>
using namespace std;
#define pii pair<int,int>
int n,g[40][40];
int mx[4] = {-1, 0, 1, 0};
int my[4] = { 0, 1, 0,-1};
bool BFS(int r, int k)
{
    bool book[40][40];
    memset(book,false,sizeof(book));
    pii q[1000];
    book[r][k] = true;
    q[0] = pii(r,k);
    for(int head = 0, tail = 1; head < tail; head++)
    {
        int x = q[head].first, y = q[head].second;
        for(int i = 0, tx, ty; i < 4; i++)
        {
            tx = x+mx[i], ty = y+my[i];
            if(tx < 1 || tx > n || ty < 1 || ty > n)
                return false;
            if(g[tx][ty] || book[tx][ty])
                continue;
            book[tx][ty] = true;
            q[tail++] = pii(tx,ty);
        }
    }
    return true;
}
void DFS(int x, int y)
{
    for(int i = 0, tx, ty; i < 4; i++)
    {
        tx = x+mx[i], ty = y+my[i];
        if(g[tx][ty])
            continue;
        g[tx][ty] = 2;
        DFS(tx,ty);
    }
    return;
}
int main()
{
    cin >> n;
    for(int i = 1; i <= n; i++)
        for(int j = 1; j <= n; j++)
            cin >> g[i][j]; 
    int sx = 0, sy = 0;
    for(int i = 1; !sx && !sy && i <= n; i++)
        for(int j = 1; j <= n; j++)
            if(!g[i][j] && BFS(i,j))
            {
                sx = i, sy = j;
                break;
            }
    g[sx][sy] = 2;
    DFS(sx,sy);
    for(int i = 1; i <= n; i++, cout << endl)
        for(int j = 1; j <= n; j++)
            cout << g[i][j] << ' '; 
    return 0;
}
