int stepx[4]={0,1,0,-1},
    stepy[4]={1,0,-1,0};
bool final=false;
void change(int w,int e)
{
    int head,tail;
    memset(q,0,sizeof(q));
    queue <int> h;
    head=0;tail=1;
    q[0].x=w;q[0].y=e;
    h.push(head);
    map2[w][e]=2;
    while(!h.empty())
    {
        head=h.front();
        for(int i=0;i<4;++i)
        {
            int x1=stepx[i]+q[head].x;
            int y1=stepy[i]+q[head].y;
            if(map2[x1][y1]==0)
            {
                q[tail].x=x1;
                q[tail].y=y1;
                map2[x1][y1]=2;
                h.push(tail);
                tail++;
            }
        }
        h.pop();
    }
}
void check(int c,int d)
{
    int s1=c,s2=d;
    while(map[c][d]==0&&c>=0)
    {
        map[c][d]==1;
        d--;
        if(d=-1) c--;
    }
    while(map[s1+1][s2+1]==0&&s1<n)
    {
        map[s1+1][s2+1]==1;
        s2++;
        if(s2==n) s1++;
    }
}
void search(int a,int b)
{
    ansx=a;ansy=b;
    int head,tail;
    memset(q,0,sizeof(q));
    queue <int> f;
    head=0;tail=1;
    q[0].x=a;q[0].y=b;
    f.push(head);
    bool turn=true;
    while (!f.empty())
    {
        head=f.front();
        for(int i=0;i<4;++i)
        {
            int x1=stepx[i]+q[head].x;
            int y1=stepy[i]+q[head].y;
            if(x1>=0&&x1<n&&y1>=0&&y1<n&&map[x1][y1]==0)
            {
                q[tail].x=x1;
                q[tail].y=y1;
                if(x1==0||x1==n||y1==0||y1==n)
                {
                    check(x1,y1);
                    turn=false;
                    break;
                }
                f.push(tail);
                tail++;
            }
        }
        if(turn==false) break;
        f.pop();
    }
    if(turn==true)
    {
        final=true;
        change(ansx,ansy);
        for(int i=0;i<n;++i)
        {
            for(int j=0;j<n;++j)
            {
                if(j<n-1) cout<<map2[i][j]<<' ';
                else cout<<map2[i][j];
            }
            cout<<endl;
        }
        return;
    }
}
int main()
{
