按照题意模拟染色过程，DFS搜索，如果搜到边界就声明染色失败，这样就可以确保所有被1围住的0都完成染色


```cpp

#include<cstdio>
#include<cstring>

using namespace std;

bool flag;
int data[100][100];
int visit[100][100];
int n;

void solve(int x,int y)
{
    if (data[x][y]==1) return;
    if (visit[x][y]==1) return;
    if ( (x==0 or x==n+1) or (y==0 or y==n+1) ) 
    {
        flag=false;
        return;
        //color failed
    }

    visit[x][y]=1;
    solve(x+1,y);
    solve(x-1,y);
    solve(x,y+1);
    solve(x,y-1);

    if (flag) data[x][y]=2;//color

    return;
}

int main()
{
    memset(data,0,sizeof(data));        
    memset(visit,0,sizeof(visit));

    scanf("%d",&n);

    for (int i=1;i<=n;i++)
        for (int j=1;j<=n;j++)
            scanf("%d",&data[i][j]);

    for (int i=1;i<=n;i++)
        for (int j=1;j<=n;j++)
            if ( (!visit[i][j]) && (data[i][j]!=1) )
            {
                flag=true;
                solve(i,j);
            }

    for (int i=1;i<=n;i++)
    {
        for (int j=1;j<=n;j++)
            printf("%d ",data[i][j]);
        printf("\n");
    }

    return 0;
}

