代码见下：

```cpp
#include<cstdio>
#include<cstring>
#include<cctype>
using namespace std;
template <class code>inline code read(const code &a)
{
    code x=0;short w=0;char ch=0;
    while(!isdigit(ch)) {w|=ch=='-';ch=getchar();}
    while(isdigit(ch)) {x=(x<<3)+(x<<1)+(ch^48);ch=getchar();}
    return w?-x:x;
}
int a[35][35];
bool used[35][35];
bool flag=1;//判断变量
int sx[4]{1,0,-1,0};
int sy[4]{0,1,0,-1};//方向数组
int n,b1,b2;
void dfs(int x,int y){
	if(x<1||y<1||x>n||y>n){//边界判断
		flag=0;
		return;
	}
	if(a[x][y]==1)return;//是1直接返回
	else used[x][y]=1;//反之标记一下已来过
	for(int i=0;i<4;i++){
		int xx=sx[i]+x,yy=sy[i]+y;
        dfs(xx,yy);//四个方向找一遍
	}
}
void rs(int x,int y){//染色函数
	if(a[x][y]==1||a[x][y]==2)return;//无论是1或2都不可继续搜索
	else a[x][y]=2;//是0直接标为2
	for(int i=0;i<4;i++){
		int xx=sx[i]+x,yy=sy[i]+y;
		rs(xx,yy);//依然四个方向找
	}
}
int main()
{
	n=read(n);
	for(int i=1;i<=n;i++){
		for(int j=1;j<=n;j++)a[i][j]=read(a[i][j]);
	}
	for(int i=1;i<=n;i++){
		for(int j=1;j<=n;j++){
			flag=1;//初始为1，若搜索时越界，会赋值为0
			if(!a[i][j])dfs(i,j);//如果当前数为0，开始搜索
			else flag=0;
			if(flag){
				b1=i,b2=j;//找到第一个搜索后发现不会越界的点，进行标记
				break;
			}
		}
		if(flag)break;
	}
	rs(b1,b2);//从标记的点开始染色
	for(int i=1;i<=n;i++){
		for(int j=1;j<=n;j++){
			printf("%d ",a[i][j]);
		}
		puts("");
	}//输出
	return 0;
}
