#include <iostream>
using namespace std;

int n;
int map[31][31];        //横着一遍,dalao们千万别学我，将数组名定义成"map"
int check[31][31];      //竖着一遍

void hanshu(int x,int y) //横着的
{
	int flag=0;
	for(int i=y;i<=n;i++)
	{
		if(flag==1)      
		{
			break;
		}
		if(map[x][i]==1 && map[x][i+1]==0)
		{
			flag=1;    //找到了第二个1，接下来要将中间的0变成2
			for(int j=i+2;j<=n;j++)
			{
				if(map[x][j]==1 && map[x][j-1]==0)
				{
					for(int k=i+1;k<=j-1;k++)
					{
						if(map[x][k]!=1)  //如果不是1，那么就是0了，就变成2
						{	
							map[x][k]=2;
						}
					}
				}
			}	
		}
	}
}



void Hanshu(int x,int y)   //跟上面的一模一样
{
	int flag1=0;
	for(int i=x;i<=n;i++)
	{
		if(flag1==1)
		{
			break;
		}
		if(check[i][y]==1 && check[i+1][y]==0)
		{
			flag1=1;
			for(int j=i+2;j<=n;j++)
			{
				if(check[j][y]==1 && check[j-1][y]==0)
				{
					for(int k=i+1;k<=j-1;k++)
					{
						if(check[k][y]!=1)
						{	
							check[k][y]=2;
						}
					}
				}
			}	
		}
	}
}



int main()
{	
	cin>>n;
	for(int i=1;i<=n;i++)
	{
		for(int j=1;j<=n;j++)
		{
			cin>>map[i][j];         //横着一遍
			check[i][j]=map[i][j];  //竖着一遍
		}
	}

	for(int i=1;i<=n;i++)
	{
		for(int j=1;j<=n;j++)
		{
			if(map[i][j]==1)
			{
				hanshu(i,j);
				Hanshu(i,j);  //两个函数名有点诡异
			}
		}
	}

	cout<<endl;   //这是方便调试的，要提交得删掉这行

	for(int i=1;i<=n;i++)
	{
		for(int j=1;j<=n;j++)
		{
			if(map[i][j]==2)  //如果横着的是2
			{
				if(check[i][j]==2)   //并且竖着的也是2
				{
					map[i][j]=2;
				}
				else
				{
					map[i][j]=0;    //那么就是2了
				}
			}

		}
	}

	for(int i=1;i<=n;i++)  //输出
	{
		for(int j=1;j<=n;j++)
		{
			if(j!=n)
			{
				cout<<map[i][j]<<" ";
			}
			else
			{
				cout<<map[i][j]<<endl;
			}
		}
	}

return 0;	//良好习惯
} 

