```cpp
#include<iostream>
#include<cstdio>
#include<cstring>
using namespace std;
int f[33][33];
int x,y,i,j,n;
bool flag[33][33];
int dx[5]={0,1,0,-1,0},dy[5]={0,0,1,0,-1};
void work(int i,int j){
    if(f[i][j]==1||flag[i][j]==1||i==n+1||i==-1||j==-1||j==n+1)return;//判断是否走到了边界或者已经走过了或者撞墙了
    int a;
    flag[i][j]=1;
    for(a=1;a<=4;a++)work(i+dx[a],j+dy[a]);//4个方向上进行查找
    return;
}
int main(){
    memset(flag,0,sizeof flag);
    scanf("%d",&n);
    for(i=1;i<=n;i++){
        for(j=1;j<=n;j++){
            scanf("%d",&f[i][j]);
            if(f[i][j])flag[i][j]=1;//给墙提前标记
        }
    }
    for(i=1;i<=n;i++){//因为在圈内的0周围肯定会有1，如此可以推导出位于边缘的0绝对不在圈内，可以作为搜索起始点
        work(i,1);//从每个边界点都搜索一遍，怎么输？！
        work(1,i);
        work(n,i);
        work(i,n);
    }
    for(i=1;i<=n;i++){
        for(j=1;j<=n;j++){
            if(flag[i][j])//判断是否走过该点，走过了就输出原数，没走过就说明在墙内，输出“2 ”(别忘了空格)
            printf("%d ",f[i][j]);
            else
                printf("2 ");
        }
    printf("\n");
    }    
    return 0;
}
