inline int read()
{
    int x=0;
    char c=getchar();
    while(c<'0'||c>'9')c=getchar();
    while(c>='0'&&c<='9')x=x*10+c-'0',c=getchar();
    return x;
}
inline void bfs(int x,int y)
{
    now.x=x;
    now.y=y;
    q.push(now);
    while(!q.empty())
    {
        nxt=q.front();
        q.pop();
        a[nxt.x][nxt.y]=3;
        for(int i=0;i<4;i++)
        {
            int xx=nxt.x+xd[i];
            int yy=nxt.y+yd[i];
