	for(int i=1;i<=n;i++)
		for(int j=m;j>=0;j--)
			if(j-a[i]>=0)
				dp[j]=dp[j]+pd[j-a[i]];
	cout<<dp[m]<<endl;//最后输出钱数为 m 时的方案总数
	return 0;
}
