	int st=1;//每一局的起点 
	for (int i=1;i<=10;i++){
		int rest=10,flag=1;//剩余rest根柱，且没有全部打完 
		for (int j=st;j<=st+2;j++){//枚举三次滚球 
			if (j>n){//如果越界退出 
				sum[i]=-1;
				break;
			}
			if (rest==0) rest=10,flag=0;//全部打完 
			if (j==st+2 && flag) break;//第三种情况，只滚两次 
			if (a[j]=='/') sum[i]+=rest,rest=0;//模拟 
			else if (a[j]>='0' && a[j]<='9') sum[i]+=a[j]-'0',rest-=a[j]-'0';//模拟 
		}
		if (sum[i]==-1) break;//越界 
		if (a[st]=='/') st++;
		else st+=2;//改变st位置 
		writep(sum[i]);
	}
	puts("");
	for (int i=1;i<=10;i++){
		if (sum[i]==-1) break;
		ans+=sum[i];
		writep(ans);
	}
    return 0;
}
