{
    int n,m;
    while(cin>>n>>m)
    {
        memset(dp,0,sizeof(dp));
        memset(tag,0,sizeof(tag));
        dp[0][1]=1;
        int x,y;
        for(int i=0;i<m;i++)
        {
            cin>>x>>y;
            tag[x][y]=1;
        }
        for(int i=1;i<=n;i++)
        {
            for(int j=1;j<=n;j++)
            {
                dp[i][j]=(dp[i-1][j]+dp[i][j-1])%mod;
                if(tag[i][j]==1) dp[i][j]=0;//如果此处有障碍物。那么是不可达 标记为0；
