```
#include<iostream>
#include<algorithm>
#include<stdio.h>
#include<cstdio>

using namespace std;
int a[2333][2333];
bool stop[2333][2333];
int dg(int x,int y){
	if(stop[x][y]){
		return 0;
	}
	if(x==1||y==1){//碰到边界
		return 1;
	}
	else{//继续递归
		return dg(x-1,y)%100003+dg(x,y-1)%100003;
	}
}
int main(){
	int n,m;
	cin>>n>>m;
	for(int i=0;i<m;i++){
		int x,y;
		cin>>x>>y;
		stop[x][y]=true;
	}
	cout<<dg(n,n)%100003<<endl;//递归
	return 0;
}
```

