#include<iostream>
#include<cstdio>
#define mod 100003 //常量咯...跟const定义没啥差别吧...
using namespace std;
int n,m,x,y;
bool f[1000+10][1000+10];//障碍点标记
long long sum[1000+10][1000+10];//方案数标记
int main()
{
	cin>>n>>m;
	for(int i=1;i<=m;i++)
	{
		scanf("%d%d",&x,&y);
		f[x][y]=1;//标记
	}
	for(int i=1;i<=n;i++)
	{
		if(!f[1][i]) sum[1][i]=1;
		else break;
	}
	for(int i=1;i<=n;i++)
	{
		if(!f[i][1]) sum[i][1]=1;
		else break;
	}
    //上述两个循环均为边界
	for(int i=2;i<=n;i++)
	{
		for(int j=2;j<=n;j++)
		{
			if(f[i][j]) continue;
			if(!f[i-1][j]) sum[i][j]=((sum[i][j]%mod)+(sum[i-1][j]%mod))%mod;
			if(!f[i][j-1]) sum[i][j]=((sum[i][j]%mod)+(sum[i][j-1]%mod))%mod;
		}
	}//递推
	cout<<sum[n][n]<<endl;
	return 0; 
} 
```

