```
#include<cstdio>
#include<iostream>
#include<cmath>
#include<cstring>
#include<string>
using namespace std;
int a[100005];
int n;
void qisort(int a[], int left, int right){
	if(left >= right){
	    return;
	}
    int key = a[left];//选定基准数
    int i = left, j = right;
    while(i != j){

		while(a[j] >= key && i < j){
		    j--;
		}

        while(a[i] <= key && i < j){
            i++;
		}

		if(i < j){
		    swap(a[i], a[j]);
		}
	}
	swap(a[left], a[i]); 
	qisort(a, left, i - 1);
	qisort(a, i + 1, right);

	return;
}
int main(){
//	freopen("qiucksort.in", "r", stdin);
    cin >> n;
    for(int i = 0; i < n; i++){
        cin >> a[i];
	}
	qisort(a, 0, n - 1);
	for(int i = 0; i < n; i++) cout << a[i] << " ";
	return 0;
}
```

