```cpp
// 防伪标识
#include<bits/stdc++.h>
using namespace std;
int l,r;
int ans = 0;
void sss(int x){ 
    if(x == 0) return; // 递归函数需要边界
    else if(x%10 == 2)ans ++;//如果扫到2出现了ans加1
    sss(x/10); // 然后把最后一位去掉接着扫
}
int main(){
    scanf("%d %d",&l,&r); // 输入l和r
    for(int i = 1;i<=r;i++){ // 循环直接扫一遍
        sss(i);
    }
    printf("%d",ans);
    return 0;
