        return n;//则返回个数 
        a=x%10;//取最后一位 
        if(a==2)//如果是2 
        n++;//计数 
        x=x/10;//把这一位去掉 
    }
}
void doing()
{
    for(int i=n;i<=m;i++)//从n~m中搜寻 
    {
        num=num+judge(i);//就是用总个数加上刚才判断的个数（即为新出了个数） 
    }
    cout<<num<<endl;//输出个数 
}
int main()
{
  cin>>n>>m;
  doing();//调用函数 
  return 0;
}
