import java.util.Scanner;

public class CountDigits {

	int countDigits(String s) {
		int c = 0;
		for(int i = 0; i < s.length(); i++) {
			String s1 = s.substring(i, i+1);
			if(s1.equals("2"))
				c++;
		}
