#include<bits/stdc++.h>
using namespace std;
map<string,int> m;//建立一个以string为关键词，int为其对应值的map
int s,n,k;
string st;
int main(){
    std::ios::sync_with_stdio(false);//关闭同步，增加cin，cou的速度
    cin>>n>>k;
    getline(cin,st);//清理冗余（就是换行）
    for (int i=1;i<=n;i++)
    {   
        getline(cin,st);//因为有空格，所以整行读入
        m[st]++;//标记
    }
    for (int i=1;i<=k;i++)
    {
        getline(cin,st);
        if (m.count(st)==1)//判断是否存在
        s++;//计数加一
    }
    cout<<s<<endl;
    return 0;
}

