```cpp
#include<iostream>
#include<cstdio>
#include<cstring>
using namespace std;
void delson(int,int,int);
void delfat(int,int,int);
void make(int,int,int);
const int N=1025;
int b[11]={1,2,4,8,16,32,64,128,256,512,1024},//初始化2的次方
    tree[20],judge[N][3*N];
char a[N][3*N];
int high1,high2,high3,high4,s=0,m,n;
int main(){
    int x,y,l,w; 
    scanf("%d %d",&m,&n);
    memset(a,' ',sizeof(a));//ASCII32，空格
    w=b[m-1]*3-1;/*总宽度（把最下层同一子树的两个节点加后面的一个空格，即【o‘ ‘’ ’‘ ’o‘ ’】看作【o‘ ’‘ ’‘ ’】*2，并在最后减去一个空格）*/
    high1=(w+1)/2;//总高度 
    make(1,high1,(high1+1)/2);//递归建树
    if (n!=0)
        for (int i=1;i<=n;i++){
            scanf("%d %d",&x,&y);
            l=0;
            high2=tree[x];// 待删除的节点所在高度 
            high3=tree[x-1];//该节点父节点高度 
            high4=tree[x+1];//该节点子节点高度 
            for (int j=1;j<=w;j++){
                if (a[high2][j]=='o'){
                    l++;
                    if (l==y) {
                        delson(high2,j,high4-high2);//删除子树
                        delfat(high2,j,high2-high3);//删除该结点与其父节点的连接
                         break;
                    }
                }    
            }
        }
    for (int i=1;i<=high1;i++){
        for (int j=1;j<=w;j++){
            if (judge[i][j]==0)
                printf("%c",a[i][j]);
            else  cout<<' ';
        }
        printf("\n");
    }
}
void make(int p,int q,int h){  //h：该结点到下一层节点的连接符号个数
    a[p][q]='o';
    if (s<=m){
        s++;tree[s]=p;
    }
    if (h==1) return;
    for (int k=1;k<h;k++){
        a[p+k][q-k]='/';
        a[p+k][q+k]='\';
    }
    make(p+h,q-h,(h+1)/2);
    make(p+h,q+h,(h+1)/2);
}
void delson(int p,int q,int h){
    judge[p][q]=1;
    if (h==0) return;
    if (h==1){
        judge[p+1][q+1]=1;
        judge[p+1][q-1]=1;
        judge[p+2][q+2]=1;
        judge[p+2][q-2]=1;
        return;
    }
    for (int k=1;k<h;k++){
        judge[p+k][q-k]=1;
        judge[p+k][q+k]=1;
    }
    delson(p+h,q-h,h/2);
    delson(p+h,q+h,h/2);    
}
void delfat(int p,int q,int h){
    for (int i=1;i<h;i++){
        judge[p-i][q-i]=1;
        judge[p-i][q+i]=1;
    }
}
