#include<iostream>
#include<cstdio>
#include<cstring>
#include<cmath>
using namespace std;
int map[1050][1050];//开个数组 
char c;
int n,m;
int ans=0;//这就是面积 
int main()
{
	memset(map,0,sizeof(map));
	scanf("%d%d",&n,&m);
	for(int i=1;i<=n;i++)
		for(int j=1;j<=m;j++)
		{
			cin>>c;//输入数字字符 
			map[i][j]=c-'0';//存数 
			if(c!='0')
				ans+=2;//计算上表面积与下表面积 
		}
	for(int i=1;i<=n;i++)//进行四个方向计算 
		for(int j=1;j<=m;j++)
		{
			if(map[i-1][j]<map[i][j])
				ans+=map[i][j]-map[i-1][j];
			if(map[i+1][j]<map[i][j])
				ans+=map[i][j]-map[i+1][j];
			if(map[i][j+1]<map[i][j])
				ans+=map[i][j]-map[i][j+1];
			if(map[i][j-1]<map[i][j])
				ans+=map[i][j]-map[i][j-1];
		}
	printf("%d",ans);//输出答案 
}
```


