			if(minn>a[j]){
				minn=a[j];
				minnn=j;
			}
		}
		a[minnn]+=x;
	}
	x=-1;
	for(int i=0;i<m;i++){
		if(a[i]>x){
			x=a[i];
		}
	}
	cout<<x;
	return 0;
