int main(){
	cin>>n>>m;
	for(int i=0;i<n;i++) cin>>b[i];
	for(int i=0;i<m;i++) a.push(b[i]);
	for(int i=m;i<n;i++){
		int ss=a.top();
		a.pop();
		a.push(b[i]+ss);
	}
