}

int minw(int a[])
{
    int min = a[1], pos = 1;
    for(int i = 2; i <= a[0]; i++)
    {
        if(a[i] < min)
        {
            min = a[i];
            pos = i;
        }
    }
    return pos;
}

int maxw(int a[])
{ 
    int max = a[1], pos = 1;
    for(int i = 2; i <= a[0]; i++)//a[0]为数组总个数 
    {
        if(a[i] > max)
        {
            max = a[i];
            pos = i;
        }
    }
    return pos; 
}
