    j=0;
    while(1){//查找正在接水的人中有没有接完的
        if(a[j]==i){a[j]+=a[m+w];w++;}//有接完的便让后一个加上来
        if(j==m-1)break;//这一排没有就退出
        j++;
    }
}
sort(a,a+n);
printf("%d",a[n-1]);
return 0;
}
