int search(int x,int y,int lc,int ld){

    int tmp=0;  //步骤如上文所述
    int ll=lc;

    for(int xx=x;xx>(x-ld);xx--){  //扫描过程
        ll=min(ll,lenc[xx][y]);
        tmp+=ll;
    }

    return tmp;
}

