using namespace std;
int h[100001],mod=100003;

int main() {
	int N,K,er=1;
	cin>>N>>K;
	for(int i=1; i<=K; i++) {
		h[i]=er;
		er=er*2%mod;
	}
	for(int i=K+1; i<=N; i++) {
		for(int j=i-K; j<i; j++) {
