int n,k,f[100005];
int main()
{
    cin>>n>>k;
    f[0]=1;//首项为0
    for(int i=1;i<=n;i++) 
    {
        for(int j=1;j<=k;j++) 
        {
            if(i>=j)
            {
                f[i]+=f[i-j];
                f[i]=f[i]%100003;//千万注意这个地方
            }
        }
    }
    cout<<f[n];
    return 0;
}
