```c
//找规律
//注意几点：K之前是等比，之后s[i]是s[i-K]加到s[i-1]的和，但处理要注意
//最大的问题是处理溢出的问题 
#include <stdio.h>
#include <math.h>
long N,K,s[100005]={0};//递推数组 
int main()
{
	int i;
	scanf("%ld%ld",&N,&K);
	s[1]=1;
	for(i=2;i<=K;i++) 
	{
		s[i]=2*s[i-1];
		s[i]%=100003;
	}
	for(i=1;i<=K;i++)
	{
		s[K+1]+=s[i];
		s[K+1]%=100003;
	}
	for(i=K+2;i<=N;i++)
	{
		s[i]=2*s[i-1]-s[i-1-K];//这一步有可能减出一个负数
		s[i]%=100003;
	}
	while(s[N]<0) s[N]+=100003;
	printf("%ld\n",s[N]);

	return 0;
}
```

