```cpp
//比较简单的递推
//（虽然名字叫dp）关系式：dp[i]=sum{dp[j]+1|i-k<=j<i};
#include<iostream>
using namespace std;
int main(){
    int n,k;cin>>n>>k;
    int dp[100005]={1};
    for(int i=1;i<=n;i++){
        int t=0;
        for(int j=(i<k?0:(i-k));j<i;j++){//注意：如果前面台阶不足K,则从0开始递推
            t+=dp[j];
        }
        t%=100003;
        dp[i]=t;
    }
    cout<<dp[n];
        return 0;
}
