map<int,int>p,s;//用map存储 
const int MAXN=1005;
int f[MAXN][MAXN];//存储每人每题提交次数 
int sc[MAXN][MAXN];//存储每个人最高分 
long long minx(long long x,long long y)
{//long long型min 
	if(x<y)
	    return x;
	return y;
}
int main()
{
	freopen("洛谷团队训练VS传统团队训练.in","r",stdin);
	freopen("洛谷团队训练VS传统团队训练.out","w",stdout);
	int n,m;
