	long long t1=n*ta;//传统 
	long long t2=n*ta;//洛谷 
	//不管什么方式都要加入题目 
	while(r--)
	{
		int pp,s1,s2;
		scanf("%d%d%d",&pp,&s1,&s2);
		pp=p[pp];
		s1=s[s1];//map 
		f[pp][s1]++;//存每人每题交的次数 
		if(e==1&&s2>sc[pp][s1])
		{
		    sc[pp][s1]=s2;//存最高分 
		    t1+=td;
		}
		t2+=tc;//洛谷评测 
	}
	for(int i=1;i<=n;i++)
	    for(int j=1;j<=m;j++)
	    {
	    	int sq=tb*f[i][j];//上去评测 
	    	int bsq=ta+tc*f[i][j];//不上去评测 
	    	t1+=minx(sq,bsq);
	    } 
	t2=(long long)t2/(a/100.0);
	t2+=h;//赖皮规则 
	printf("%lld\n%lld\n",t1,t2);
	if(t1<t2)//比较大小 
