```
#include <cstdio>
struct nodea{ int x,y,g; } b[1000001];
struct nodeb{ int h; } r[1000001];
int ans[1000001],f[1000001],c[1000001],x[1000001];
int len=0,da=0,p=0;
int n=0,m=0;
void ins(int x,int y)
{
	len++;
	b[len].x=x;
	b[len].y=y;
	b[len].g=r[x].h;
	r[x].h=len;
}
int find(int x)
{
	if(x==f[x])
	{
		return x;
	}
	else
	{
		return f[x]=find(f[x]);
	}
}
void hb(int x,int y)
{
	int tx=find(x),ty=find(y);
	if(tx!=ty)
	{
		f[tx]=ty;
	}
}
int main()
{
	scanf("%d %d",&n,&m);
	for(int i=1;i<=n;i++)
	{
		f[i]=i;
	}
	for(int i=1;i<=m;i++)
	{
		int x=0,y=0;
		scanf("%d %d",&x,&y);
		x++,y++;
		ins(x,y),ins(y,x);
	}
	int q=0;
	scanf("%d",&q);
	for(int i=1;i<=q;i++)
	{
		scanf("%d",&x[i]);
		x[i]++;
		c[x[i]]=1;
	}
	for(int k=1;k<=n;k++)
	{
		if(c[k]==0)
		{
			for(int i=r[k].h;i>0;i=b[i].g)
			{
				if(c[b[i].x]==0 && c[b[i].y]==0)
				{
					hb(b[i].x,b[i].y);
				}
			}
		}
	}
	for(int i=1;i<=n;i++)
	{
		if(c[i]==0 && f[i]==i)
		{
			da++;
		}
	}
	ans[++p]=da;
	for(int j=q;j>=1;j--)
	{
		da++;
		c[x[j]]=0;
		for(int i=r[x[j]].h;i>0;i=b[i].g)
		{
			if(c[b[i].x]==0 && c[b[i].y]==0 && find(b[i].x)!=find(b[i].y))
			{
				da--;
				hb(b[i].x,b[i].y);
			}
		}
		ans[++p]=da;
	}
	for(int i=p;i>=1;i--)
	{
		printf("%d\n",ans[i]);
	}
	return 0;
}
