{
	e[++tot]=(data){head[u],v};
	head[u]=tot;
}
int f[maxn],n,m,v[maxn],k,a[maxn],cnt,ans[maxn];
int find(int x)
{
	return x==f[x]?x:f[x]=find(f[x]);
}
void dfs(int x)
{
	v[x]=1;
	for (int i=head[x];i;i=e[i].nxt)
	{
		if (!v[e[i].to])
		{
			int f1=find(x),f2=find(e[i].to);
			if (f1!=f2) f[f1]=f2;
			dfs(e[i].to);
		}
	}
}
int main()
{
	scanf("%d%d",&n,&m);
	for (int i=0;i<n;++i) f[i]=i;
	for (int i=0;i<m;++i)
	{
		int u,v;
