		add(u,v); add(v,u);
	}
	scanf("%d",&k);
	for (int i=0;i<k;++i) scanf("%d",&a[i]),v[a[i]]=1;
	for (int i=0;i<n;++i)
	{
		if (!v[i])
		{
			cnt++;
			dfs(i);
		}
	}
	ans[k]=cnt;
	memset(v,0,sizeof(v));
	for (int i=0;i<k;++i) v[a[i]]=1;
	for (int i=k-1;i>=0;--i)
	{
		v[a[i]]=0; 
		cnt++;
		for (int j=head[a[i]];j;j=e[j].nxt)
		{
			if (!v[e[j].to])
			{
				int f1=find(a[i]),f2=find(e[j].to);
				if (f1!=f2)
				{
					f[f2]=f1;
					cnt--;
				}
			}
		}
		ans[i]=cnt;
	}
