}
void hb(int x,int y)
{
    int t1=find(x),t2=find(y);
    f[t2]=t1;
}
void reproduction(int x)
//枚举x的边，他联通的点全都成为他的儿子 
{
    vis[x]=1;
    for(int i=head[x];i;i=e[i].next)
    {
        int v=e[i].to;
        if(vis[v]||book[v])continue;
        f[v]=find(x);
