    for(ll i=0; i<n; i++){
        if(b[i]){
            father[i]=searchfather(i);
            if(father[i]==i) sum++;
        }
    }
    return sum;	
}

int main(){
    scanf("%lld %lld",&n,&m);
    for(ll i=0; i<n; i++) father[i]=i;
    while(m--){
        ll x,y;
        scanf("%lld %lld",&x,&y);
        q[x].push_back(y);
        q[y].push_back(x);
    }

    scanf("%lld",&k);
    memset(b,true,sizeof(b));
    for(ll i=1; i<=k; i++){
        scanf("%lld",&fire[i]);
        b[fire[i]]=false;
    }

    for(ll i=0; i<n; i++){
        if(b[i]){
            for(ll j=0; j<q[i].size(); j++){
                ll Next=q[i][j];
                if(b[Next]){
                    ll f1=searchfather(i),f2=searchfather(Next);
                    if(father[f1]!=f2) father[f1]=f2;
                }
            }	
        }
    }

    now=find();
    for(ll i=k; i>=1; i--){
        ans[i]=now;
        ll x=fire[i]; b[x]=true;
        now++;
        //因为合并计数的时候把当前的点也算进去了，而now中不包含，多-1，所以要加上去
        for(ll j=0; j<q[x].size(); j++){
            ll y=q[x][j];
            if(b[y]){
                ll f1=searchfather(x),f2=searchfather(y);
                if(father[f1]!=f2){
