{
    if(father[x]==x)
    {
        return x;
    }
    else
    {
        int mem=find(father[x]);
        father[x]=mem;
        return mem;
    }
}
int main()
{
    int n,m,i,k,x,y,no,cont=0,a,b;
    scanf("%d%d",&n,&m);
    for(i=0;i<n;i++)
    {
        father[i]=i;
    }
    for(i=1;i<=m;i++)
    {
        scanf("%d%d",&x,&y);
        ne[i*2-1].num=y;
        ne[i*2-1].nex=h[x];
        h[x]=i*2-1;
        ne[i*2].num=x;
        ne[i*2].nex=h[y];
        h[y]=i*2;
    }
    scanf("%d",&k);
    for(i=1;i<=k;i++)
    {
        scanf("%d",&x);
        flag[x]=1;
        s.push(x);
    }
    for(i=0;i<n;i++)
    {
        if(flag[i]==1)
        {
            continue;
        }
        a=find(i);
        for(no=h[i];no!=0;no=ne[no].nex)
        {
            if(flag[ne[no].num]==1)
            {
                continue;
            }
            b=find(ne[no].num);
            if(a!=b)
            {
                father[a]=b;
                a=b;
                cont++;
            }
        }
    }
    ans.push(n-cont-k);
    int top;
    for(i=1;i<=k;i++)
    {
        top=s.top();
        s.pop();
        flag[top]=0;
        a=find(top);
        for(no=h[top];no!=0;no=ne[no].nex)
        {
            if(flag[ne[no].num]==1)
            {
                continue;
            }
            b=find(ne[no].num);
            if(a!=b)
            {
                father[a]=b;
                a=b;
                cont++;
            }
        }
        ans.push(n-cont-(k-i));
    }
    for(i=1;i<=k+1;i++)
    {
        printf("%d\n",ans.top());
        ans.pop();
    }
    return 0;
}
