using namespace std;
const int MM=2e5+10;
int an[2*MM],f[2*MM],ma[2*MM];
int getroot(int p)
{
    if(p==f[p])
        return p;
    return f[p]=getroot(f[p]);
}
void merge(int fi,int se,int p)
{
    int u=getroot(fi),v=getroot(se);
    if(u!=v)
        an[p]--;
    f[u]=v;
}
struct No{
	int pri,a,b;
	bool operator <(const No & r)const
	{
		return pri<r.pri;
	}
}e[MM];
int main()
{
    int n,m,k;
    cin>>n>>m;
    for(int i=0;i<n;i++)
        f[i]=i;
    for(int i=1;i<=m;i++)
        cin>>e[i].a>>e[i].b;
    cin>>k;
    for(int i=1;i<=k;i++)
    {
    	int x;
        cin>>x;
        ma[x]=1+k-i;
    }
    for(int i=1;i<=m;i++)
    	e[i].pri=max(ma[e[i].a],ma[e[i].b]);
    sort(e+1,e+m+1);
    int p=1;
    an[0]=n-k;
    for(int i=0;i<=k;i++)
    {
    	while(e[p].pri==i&&p<=m)
        	merge(e[p].a,e[p].b,i),p++;
        an[i+1]=an[i]+1;
	}
    for(int i=k;i>=0;i--)
        cout<<an[i]<<endl;
}
