			}
		}
		ans.push(tmp);
	}
	while(!ans.empty()){
		cout<<ans.top()<<endl;
		ans.pop();
	}
	return 0;
}
int find(int x)
{
	return fatr[x]==x? x:fatr[x]=find(fatr[x]);
