```
#include<stdio.h>
#include<iostream>
#include<cstring>
#include<stack>
#define MAXN 400005
#define MAXM 200005
using namespace std;
int n,m,k,i,j,x,y,tot,f[MAXN],d[MAXN];
bool del[MAXN];
//f数组记录并查集，d数组记录删除星球的顺序，del数组记录该星球是否被删除 
struct edge{
	int v;
	edge* next;
}temp[MAXM*2], *h[MAXN];
void AddEdge(int u, int v)
{
	edge *p = &temp[tot++], *q = &temp[tot++];
	p->v = v; p->next = h[u]; h[u] = p;
	q->v = u; q->next = h[v]; h[v] = q;
}
int GetFather(int a) //包含路径压缩
{
	if(f[a] < 0) return a;
	return f[a] = GetFather(f[a]);
}
bool Join(int a, int b)
{
	//printf("Join %d %d", a, b);
	int fa = GetFather(a);
	int fb = GetFather(b);
	if(fa == fb) return false;
	int temp = f[fa] + f[fb];
	if(fa < fb) //fa中的星球数量较多，把fb合并到fa中 
	    f[fb] = fa, f[fa] = temp;
	else
		f[fa] = fb, f[fb] = temp;
	return true;
}
int main()
{
	//读入数据并初始化 
	memset(f, -1, sizeof(f));
	scanf("%d %d", &n, &m);
	for(i = 1;i <= m; i++)
	{
		scanf("%d %d", &x, &y);
		AddEdge(++x, ++y);
	}
	scanf("%d", &k);
	for(i = 1;i <= k; i++)
	{
		scanf("%d", &x);
		d[i] = ++x; //输入的星球编号从0开始，这里处理成从1开始
		del[x] = 1;
	}
	/*for(i = 1;i <= n; i++)
		printf("%d ", del[i]);
	printf("\n");*/
	//以线下的方式处理数据 ，将结果压入栈里
	stack <int> q; 
	for(i = 1;i <= n; i++)
	{
		if(del[i]) continue;
		for(edge* p = h[i]; p; p=p->next)
			if(del[p->v] == 0) Join(i, p->v);
	}
	int ans = 0;
	for(i = 1;i <= n; i++)
		if(f[i]<0 && del[i]==0) ans++;
	q.push(ans);
	/*for(i = 1;i <= n; i++)
		printf("%d ", f[i]);
	printf("\nans: %d\n", ans);
	system("pause");*/
	for(i = k;i > 0; i--) //倒序将被摧毁的星球加入并查集 
	{
		del[d[i]] = 0;
		ans++;
		for(edge *p = h[d[i]]; p; p=p->next)
			if(del[p->v] == 0 && Join(d[i], p->v)) ans--;
		q.push(ans);
		//printf("\nans: %d\n", ans);
		//system("pause");
	}
	while(!q.empty())
	{
		printf("%d\n", q.top());
		q.pop();
	}
	return 0;
}
